        $('#tbody').empty()
        $.getJSON('/direksatisilaclari/listesi', (data) => {
            if (data.basarili) {
                liste = data.liste
                data.liste.map(item => $('#tbody').append(`
        <tr>
            <td>${item.id}</td>
            <td>${item.ilacid}</td>
            <td>${item.adet}</td>
            
           
   ");
